import React, { useState } from 'react'

const Input = () => {

    //get form input value using state
    const [input, setInput] = useState({
        name : '',
        email: '',
        cell : '',
        uname : ''
    });

    //alert management
    const [ alert, setAlert] = useState({
        msg : 'This is an alert',
        type : 'danger',
        status: false

    });


    // validating the form
    const handleFormSubmit = (e) => {
        e.preventDefault();

        if( input.name === '' || input.email === '' || input.cell === ''|| input.uname === ''){
            setAlert({
                msg : 'Al fielda are required',
                type: 'danger',
                status: true
            })
        }
        else{
            setAlert({
                msg : 'Data Stable',
                type: 'success',
                status: true
            });

            //clearing fields
            setInput({
                name : '',
                email: '',
                cell : '',
                uname : ''
            })

        }
    }
    //Alert close

    const handleALertClose = () => {
        setAlert({
            msg : '',
            type: 'danger',
            status: false
        })
    }

  return (

    <>
        <div className="container">
            <div className="row">
                <div className="col-md-4">
                    <div className="card shadow-sm">
                        <div className="card-header">
                            <h2>Create New User</h2>
                        </div>
                        <div className="card-body">

                            {
                                alert.status && <p className={ `alert alert-${alert.type} d-flex justify-content-between` } >{ alert.msg } <button onClick={ handleALertClose }
                                className='btn-close' ></button></p>
                            }


                            <form action="" onSubmit={ handleFormSubmit }>
                                <div className="my-3">
                                    <label htmlFor="">Name</label>
                                    <input type="text" className='form-control' value={ input.name } onChange={ e => setInput( {...input, name:e.target.value} )}/>
                                </div> 
                                <div className="my-3">
                                    <label htmlFor="">Email</label>
                                    <input type="text" className='form-control' value={ input.email } onChange={ e => setInput( {...input, email:e.target.value} )}/>
                                </div>
                                <div className="my-3">
                                    <label htmlFor="">Cell</label>
                                    <input type="text" className='form-control' value={ input.cell } onChange={ e => setInput( {...input, cell:e.target.value} )}/>
                                </div>
                                <div className="my-3">
                                    <label htmlFor="">Username</label>
                                    <input type="text" className='form-control' value={ input.uname } onChange={ e => setInput( {...input, uname:e.target.value})}/>
                                </div>
                                <div className="my-3">
                                    <label htmlFor="">Gender</label>
                                </div>
                                <div className="my-3">

                                    <input type="submit" className='btn btn-primary'  />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
                <div className="col-md-8">

                </div>
            </div>
        </div>
    </>
  )
}

export default Input
